INSERT INTO `bk_hotel_booking_multi_lang` (`id`, `foreign_id`, `model`, `locale`, `field`, `content`, `source`) VALUES('1246', '1423', 'pjField', '1', 'title', 'Email notifications will be sent to people who make a reservation after reservation form is completed or/and payment is made. If you leave subject field blank no email will be sent. You can use tokens in the email messages to personalize them.<br /><br /><br /><table width="100%" border="0" cellspacing="0" cellpadding="0">  <tbody><tr>    <td width="50%" valign="top"><p>{Title} - customer title;<br />      {FirstName} - customer first name;<br />      {LastName} - customer last name; <br />      {Phone} - customer phone number; <br />      {Email} - customer e-mail address; <br />      {ArrivalTime} - arrival time; <br />      {Notes} - additional notes; <br />      {Company} - company; <br />      {Address} - address; <br />      {City} - city; <br />      {State} - state; <br />      {Zip} - zip code; <br />      {Country} - country; <br />{BookingID} - Booking ID; <br />      {DateFrom} - Booking start date; <br />{DateTo} - Booking end date; <br />{Rooms} - selected rooms; <br />{Extras} - selected room extras; </p></td>    <td width="50%" valign="top">{CCType} - CC type; <br />      {CCNum} - CC number; <br />      {CCExpMonth} - CC exp.month; <br />      {CCExpYear} - CC exp.year; <br />      {CCSec} - CC sec. code; <br />      {PaymentMethod} - selected payment method; <br />      {Deposit} - Deposit amount; <br />      {SecurityDeposit} - Security deposit amount; <br />{Tax} - Tax amount; <br />      {Total} - Total amount; <br />      {RoomPrice} - Room(s) price; <br />      {ExtraPrice} - Rooms extras price; <br />      {Discount} - Discount amount; <br />      {CancelURL} - Link for booking cancellation;</td>  </tr></tbody></table>', 'script');
INSERT INTO `bk_hotel_booking_multi_lang` (`id`, `foreign_id`, `model`, `locale`, `field`, `content`, `source`) VALUES('1248', '1423', 'pjField', '3', 'title', 'Las notificaciones por correo electrónico seran enviadas a las personas que realicen una reservación despues de completar el formulario de reserva o/y el pago sea realizado. Si dejas el campo de asunto en blanco el correo no sera enviado. Puedes usar tokens en el mensaje del correo para personalizarlo.<br /><br /><br /><table width="100%" border="0" cellspacing="0" cellpadding="0">  <tbody><tr>    <td width="50%" valign="top"><p>{Title} - Titulo de cliente;<br />      {FirstName} - Primer nombre del cliente;<br />      {LastName} - Apellido del cliente; <br />      {Phone} - Teléfono del cliente; <br />      {Email} - Correo electrónico del cliente; <br />      {ArrivalTime} - Hora de llegada; <br />      {Notes} - Notas adicionales; <br />      {Company} - Compañia; <br />      {Address} - Direcciones; <br />      {City} - Ciudad; <br />      {State} - Departamento; <br />      {Zip} - Código postal; <br />      {Country} - País; <br />{BookingID} - ID de reservación; <br />      {DateFrom} - Fecha inicio de reservación; <br />{DateTo} - Fecha termino de reservación; <br />{Rooms} - Habitación seleccionada; <br />{Extras} - Extras de habitación seleccionadas; </p></td>    <td width="50%" valign="top">{CCType} - Tipo de tarjeta de credito; <br />      {CCNum} - Numero de tarjeta de credito; <br />      {CCExpMonth} - Mes de expiración de la tarjeta de credito; <br />      {CCExpYear} - Año de expiración de la tarjeta de credito; <br />      {CCSec} - Código de seguridad de la tarjeta de credito; <br />      {PaymentMethod} - Metodo de pago seleccionado; <br />      {Deposit} - Valor de deposito; <br />      {SecurityDeposit} - Valor de deposito de seguridad; <br />{Tax} - Valor de la tarifa; <br />      {Total} - Valor total; <br />      {RoomPrice} - Precio de habitacion(es); <br />      {ExtraPrice} - Precio de extras de habitación; <br />      {Discount} - Valor del descuento; <br />      {CancelURL} - Enlace para cancelación de la reserva;</td>  </tr></tbody></table>', 'script');
