INSERT INTO `bk_hotel_booking_multi_lang` (`id`, `foreign_id`, `model`, `locale`, `field`, `content`, `source`) VALUES('8232', '3388', 'pjField', '1', 'title', 'Use the editor below to create a template for your invoices. Include different tokens which will be replaced by invoice data.<br /><br /><br /> <table width="100%" border="0" cellpadding="5">   <tr>     <td align="left" valign="top" width="50%">{uuid} - invoice unique ID<br />      t{order_id} - order ID          {issue_date} - invoice issue date<br />         {due_date} - payment due date<br />         {created} - created date and time<br />         {modified} - update date and time<br />         {status} - invoice status<br />         {subtotal} - sub total amount<br />         {discount} - discount amount<br />         {tax} - tax amount<br />         {shipping} - shipping amount<br />         {total} - total amount<br />         {paid_deposit} - paid deposit amount<br />         {amount_due} - amount due<br />         {currency} - currency<br />         {notes} - notes<br /> t    {items} - items         <strong>Company details</strong><br />         {y_logo} - logo<br />         {y_company} - company name<br />         {y_name} - name<br />         {y_street_address} - address<br />         {y_country} - country<br />         {y_city} - city<br />         {y_state} - state<br />         {y_zip} - zip<br />         {y_phone} - phone<br />         {y_fax} - fax<br />         {y_email} - email<br />         {y_url} - url</td>     <td align="left" valign="top" width="50%">     <strong>Client billing details</strong><br />     {b_billing_address} - address<br />     {b_company} - company<br />     {b_name} - name<br />     {b_address} - address<br />     {b_street_address} - street address<br />     {b_country} - country<br />     {b_city} - city<br />     {b_state} - state<br />     {b_zip} - zip<br />     {b_phone} - phone<br />     {b_fax} - fax<br />     {b_email} - email<br />     {b_url} - url<br />     <strong>Client shipping details</strong><br />     {s_shipping_address} - address<br />     {s_company} - company<br />     {s_name} - name<br />     {s_address} - address<br />     {s_street_address} - street address<br />     {s_country} - country<br />     {s_city} - city<br />     {s_state} - state<br />     {s_zip} - zip<br />     {s_phone} - phone<br />     {s_fax} - fax<br />     {s_email} - email<br />     {s_url} - url<br />     {s_date} - shipping date<br />     {s_terms} - shipping terms<br />     {s_is_shipped} - is shipped status<br />     </td>   </tr> </table>', 'plugin');
INSERT INTO `bk_hotel_booking_multi_lang` (`id`, `foreign_id`, `model`, `locale`, `field`, `content`, `source`) VALUES('8234', '3388', 'pjField', '3', 'title', 'Utiliza el editor de abajo para crear una plantilla para tus facturas. Incluye diferentes token los cuales serán reemplazados por los datos de la factura.<br /><br /><br /> <table width="100%" border="0" cellpadding="5">   <tr>     <td align="left" valign="top" width="50%">{uuid} - ID único de factura<br />      t{order_id} - ID de compra          {issue_date} - fecha de emisión <br />         {due_date} - Fecha de vencimiento<br />         {created} - Fecha y hora de creación<br />         {modified} - Fecha y hora de actualización<br />         {status} - Estado de la factura<br />         {subtotal} - Subtotal<br />         {discount} - Descuento<br />         {tax} - Impuesto<br />         {shipping} - Monto de Envío<br />         {total} - Total<br />         {paid_deposit} - Deposito pagado<br />         {amount_due} - Cantidad adeudada<br />         {currency} - Moneda<br />         {notes} - Observaciones<br /> t    {items} - items         <strong>Detalles de empresa</strong><br />         {y_logo} - Logotipo<br />         {y_company} - Nombre de empresa<br />         {y_name} - Nombre<br />         {y_street_address} - Dirección<br />         {y_country} - País<br />         {y_city} - Ciudad<br />         {y_state} - Estado<br />         {y_zip} - Zip<br />         {y_phone} - Teléfono<br />         {y_fax} - Fax<br />         {y_email} - Correo Electrónico<br />         {y_url} - URL</td>     <td align="left" valign="top" width="50%">     <strong>Detalles de facturación del cliente</strong><br />     {b_billing_address} - Dirección<br />     {b_company} - Empresa<br />     {b_name} - Nombre<br />     {b_address} - address<br />     {b_street_address} - Dirección<br />     {b_country} - País<br />     {b_city} - Ciudad<br />     {b_state} - Estado<br />     {b_zip} - Zip<br />     {b_phone} - Teléfono<br />     {b_fax} - Fax<br />     {b_email} - Correo electrónico<br />     {b_url} - URL<br />     <strong>Detalles de envío del cliente</strong><br />     {s_shipping_address} - Dirección<br />     {s_company} - Empresa<br />     {s_name} - Nombre<br />     {s_address} - Dirección<br />     {s_street_address} - Dirección<br />     {s_country} - País<br />     {s_city} - Ciudad<br />     {s_state} - Estado<br />     {s_zip} - Zip<br />     {s_phone} - Teléfono<br />     {s_fax} - Fax<br />     {s_email} - Correo electrónico<br />     {s_url} - URL<br />     {s_date} - Fecha de envío<br />     {s_terms} - Términos de envío<br />     {s_is_shipped} - Estado Incluye gastos de envío<br />     </td>   </tr> </table>', 'plugin');
