INSERT INTO `bk_hotel_booking_multi_lang` (`id`, `foreign_id`, `model`, `locale`, `field`, `content`, `source`) VALUES('9915', '3700', 'pjField', '1', 'title', 'Email notifications will be sent to people who make a reservation after reservation form is completed or/and payment is made. If you leave subject field blank no email will be sent. You can use tokens in the email messages to personalize them.n<br /><br /><br />n<table width="100%" border="0" cellspacing="0" cellpadding="0">  n<tbody><tr>    n<td width="33%" valign="top">{Title} - customer title;<br />{FirstName} - customer first name;<br />{LastName} - customer last name; <br />{Phone} - customer phone number; <br />{Email} - customer e-mail address; <br />{ArrivalTime} - arrival time; <br />{Notes} - additional notes; <br />{Company} - company; <br />{Address} - address; <br />{City} - city; <br />{State} - state;</td>n<td width="33%" valign="top">{Zip} - zip code; <br />{Country} - country; <br />{BookingID} - Booking ID; <br />{DateFrom} - Booking start date; <br />{DateTo} - Booking end date; <br />{Rooms} - selected rooms; <br />{Extras} - selected room extras;<br />{CCType} - CC type; <br />{CCNum} - CC number; <br />{CCExpMonth} - CC exp.month; <br />{CCExpYear} - CC exp.year;</td>n<td width="33%" valign="top">{CCSec} - CC sec. code; <br />{PaymentMethod} - selected payment method; <br />{Deposit} - Deposit amount; <br />{SecurityDeposit} - Security deposit amount; <br />{Tax} - Tax amount; <br />{Total} - Total amount; <br />{RoomPrice} - Room(s) price; <br />{ExtraPrice} - Rooms extras price; <br />{Discount} - Discount amount; <br />{CancelURL} - Link for booking cancellation;</td>n</tr></tbody></table>', 'script');
INSERT INTO `bk_hotel_booking_multi_lang` (`id`, `foreign_id`, `model`, `locale`, `field`, `content`, `source`) VALUES('9917', '3700', 'pjField', '3', 'title', 'Las notificaciones de correo electrónico serán enviadas a las personas que realicen las reservaciones luego de que el formulario de reservación sea completado y/o el pago sea realizado. Si dejaste un campo en blanco, el correo electrónico no será enviado. Puedes utilizar los tokens en los mensajes de correo electrónico para personalizarlos.n<br /><br /><br />n<table width="100%" border="0" cellspacing="0" cellpadding="0">  n<tbody><tr>    n<td width="33%" valign="top">{Title} - Título del cliente;<br />{FirstName} - Primer nombre del cliente;<br />{LastName} - Apellido del cliente; <br />{Phone} - Número telefónico del cliente; <br />{Email} - Correo electrónico del cliente; <br />{ArrivalTime} - Hora llegada; <br />{Notes} - Observaciones adicionales; <br />{Company} - Empresa; <br />{Address} - Dirección; <br />{City} - Ciudad; <br />{State} - Estado;</td>n<td width="33%" valign="top">{Zip} - Código Zip; <br />{Country} - País; <br />{BookingID} - ID de Reserva; <br />{DateFrom} - Fecha inicio de reserva; <br />{DateTo} - Fecha fin de reserva; <br />{Rooms} - habitaciones seleccionadas; <br />{Extras} - extras de habitaciones seleccionados;<br />{CCType} - tipo de tarjeta de crédito; <br />{CCNum} - Número de tarjeta de crédito; <br />{CCExpMonth} - Mes de expiración de tarjeta de crédito; <br />{CCExpYear} - Año de expiración de tarjeta de crédito;</td>n<td width="33%" valign="top">{CCSec} - Código de seguridad de tarjeta de crédito; <br />{PaymentMethod} - método de pago seleccionado; <br />{Deposit} - Monto de depósito; <br />{SecurityDeposit} - Monto de depósito de seguridad; <br />{Tax} - Monto por impuesto; <br />{Total} - Total; <br />{RoomPrice} - Precio de habitación(es); <br />{ExtraPrice} - Precio de extras de habitación(es); <br />{Discount} - Valor de Descuento; <br />{CancelURL} - Enlace para cancelación de reserva;</td>n</tr></tbody></table>', 'script');
