INSERT INTO `bk_hotel_booking_multi_lang` (`id`, `foreign_id`, `model`, `locale`, `field`, `content`, `source`) VALUES('8232', '3388', 'pjField', '1', 'title', 'Use the editor below to create a template for your invoices. Include different tokens which will be replaced by invoice data.<br /><br /><br /> <table width="100%" border="0" cellpadding="5">   <tr>     <td align="left" valign="top" width="50%">{uuid} - invoice unique ID<br />      t{order_id} - order ID          {issue_date} - invoice issue date<br />         {due_date} - payment due date<br />         {created} - created date and time<br />         {modified} - update date and time<br />         {status} - invoice status<br />         {subtotal} - sub total amount<br />         {discount} - discount amount<br />         {tax} - tax amount<br />         {shipping} - shipping amount<br />         {total} - total amount<br />         {paid_deposit} - paid deposit amount<br />         {amount_due} - amount due<br />         {currency} - currency<br />         {notes} - notes<br /> t    {items} - items         <strong>Company details</strong><br />         {y_logo} - logo<br />         {y_company} - company name<br />         {y_name} - name<br />         {y_street_address} - address<br />         {y_country} - country<br />         {y_city} - city<br />         {y_state} - state<br />         {y_zip} - zip<br />         {y_phone} - phone<br />         {y_fax} - fax<br />         {y_email} - email<br />         {y_url} - url</td>     <td align="left" valign="top" width="50%">     <strong>Client billing details</strong><br />     {b_billing_address} - address<br />     {b_company} - company<br />     {b_name} - name<br />     {b_address} - address<br />     {b_street_address} - street address<br />     {b_country} - country<br />     {b_city} - city<br />     {b_state} - state<br />     {b_zip} - zip<br />     {b_phone} - phone<br />     {b_fax} - fax<br />     {b_email} - email<br />     {b_url} - url<br />    