INSERT INTO `bk_hotel_booking_multi_lang` (`id`, `foreign_id`, `model`, `locale`, `field`, `content`, `source`) VALUES('9915', '3700', 'pjField', '1', 'title', 'Email notifications will be sent to people who make a reservation after reservation form is completed or/and payment is made. If you leave subject field blank no email will be sent. You can use tokens in the email messages to personalize them.n<br /><br /><br />n<table width="100%" border="0" cellspacing="0" cellpadding="0">  n<tbody><tr>    n<td width="33%" valign="top">{Title} - customer title;<br />{FirstName} - customer first name;<br />{LastName} - customer last name; <br />{Phone} - customer phone number; <br />{Email} - customer e-mail address; <br />{ArrivalTime} - arrival time; <br />{Notes} - additional notes; <br />{Company} - company; <br />{Address} - address; <br />{City} - city; <br />{State} - state;</td>n<td width="33%" valign="top">{Zip} - zip code; <br />{Country} - country; <br />{BookingID} - Booking ID; <br />{DateFrom} - Booking start date; <br />{DateTo} - Booking end date; <br />{Rooms} - selected rooms; <br />{Extras} - selected room extras;<br />{CCType} - CC type; <br />{CCNum} - CC number; <br />{CCExpMonth} - CC exp.month; <br />{CCExpYear} - CC exp.year;</td>n<td width="33%" valign="top">{CCSec} - CC sec. code; <br />{PaymentMethod} - selected payment method; <br />{Deposit} - Deposit amount; <br />{SecurityDeposit} - Security deposit amount; <br />{Tax} - Tax amount; <br />{Total} - Total amount; <br />{RoomPrice} - Room(s) price; <br />{ExtraPrice} - Rooms extras price; <br />{Discount} - Discount amount; <br />{CancelURL} - Link for booking cancellation;</td>n</tr></tbody></table>', 'script');
INSERT INTO `bk_hotel_booking_multi_lang` (`id`, `foreign_id`, `model`, `locale`, `field`, `content`, `source`) VALUES('9917', '3700', 'pjField', '3', 'title', 'Las notificaciones de correo electrnico sern enviadas a las personas que realicen las reservaciones luego de que el formulario de reservacin sea completado y/o el pago sea realizado. Si dejaste un campo en blanco, el correo electrnico no ser enviado. Puedes utilizar los tokens en los mensajes de correo electrnico para personalizarlos.n<br /><br /><br />n<table width="100%" border="0" cellspacing="0" cellpadding="0">  n<tbody><tr>    n<td width="33%" valign="top">{Title} - Ttulo del cliente;<br />{FirstName} - Primer nombre del cliente;<br />{LastName} - Apellido del cliente; <br />{Phone} - Nmero telefnico del cliente; <br />{Email} - Correo electrnico del cliente; <br />{ArrivalTime} - Hora llegada; <br />{Notes} - Observaciones adicionales; <br />{Company} - Empresa; <br />{Address} - Direccin; <br />{City} - Ciudad; <br />{State} - Estado;</td>n<td width="33%" valign="top">{Zip} - Cdigo Zip; <br />{Country} - Pas; <br />{BookingID} - ID de Reserva; <br />{DateFrom} - Fecha inicio de reserva; <br />{DateTo} - Fecha fin de reserva; <br />{Rooms} - habitaciones seleccionadas; <br />{Extras} - extras de habitaciones seleccionados;<br />{CCType} - tipo de tarjeta de crdito; <br />{CCNum} - Nmero de tarjeta de crdito; <br />{CCExpMonth} - Mes de expiracin de tarjeta de crdito; <br />{CCExpYear} - Ao de expiracin de tarjeta de crdito;</td>n<td width="33%" valign="top">{CCSec} - Cdigo de seguridad de tarjeta de crdito; <br />{PaymentMethod} - mtodo de pago seleccionado; <br />{Deposit} - Monto de depsito; <br />{SecurityDeposit} - Monto de depsito de seguridad; <br />{Tax} - Monto por impuesto; <br />{Total} - Total; <br />{RoomPrice} - Precio de habitacin(es); <br />{ExtraPrice} - Precio de extras de habitacin(es); <br />{Discount} - Valor de Descuento; <br />{CancelURL} - Enlace para cancelacin de reserva;</td>